"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([[5246],{4527:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"docSidebar":[{"type":"link","label":"Cabbage v3 Documentation","href":"/cabbage3docs/docs/intro","docId":"intro","unlisted":false},{"type":"link","label":"Download and Install","href":"/cabbage3docs/docs/download","docId":"download","unlisted":false},{"type":"link","label":"What\'s new in v.3","href":"/cabbage3docs/docs/cabbage3","docId":"cabbage3","unlisted":false},{"type":"category","label":"Using Cabbage 3","items":[{"type":"link","label":"Overview","href":"/cabbage3docs/docs/using_cabbage/overview","docId":"using_cabbage/overview","unlisted":false},{"type":"link","label":"Running Cabbage","href":"/cabbage3docs/docs/using_cabbage/running_cabbage","docId":"using_cabbage/running_cabbage","unlisted":false},{"type":"link","label":"Working with Instruments","href":"/cabbage3docs/docs/using_cabbage/working_with_instruments","docId":"using_cabbage/working_with_instruments","unlisted":false},{"type":"link","label":"Exporting Instruments","href":"/cabbage3docs/docs/using_cabbage/exporting_instruments","docId":"using_cabbage/exporting_instruments","unlisted":false},{"type":"link","label":"Reserved Channels","href":"/cabbage3docs/docs/using_cabbage/reserved_channels","docId":"using_cabbage/reserved_channels","unlisted":false},{"type":"link","label":"Working with Media Files","href":"/cabbage3docs/docs/using_cabbage/media_files","docId":"using_cabbage/media_files","unlisted":false},{"type":"link","label":"Working with Multichannel Instruments","href":"/cabbage3docs/docs/using_cabbage/multichannel","docId":"using_cabbage/multichannel","unlisted":false},{"type":"link","label":"Commands & Settings","href":"/cabbage3docs/docs/using_cabbage/commands","docId":"using_cabbage/commands","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Widgets","items":[{"type":"link","label":"Cabbage Widgets","href":"/cabbage3docs/docs/cabbage_widgets/intro","docId":"cabbage_widgets/intro","unlisted":false},{"type":"link","label":"Form","href":"/cabbage3docs/docs/cabbage_widgets/form","docId":"cabbage_widgets/form","unlisted":false},{"type":"link","label":"Button","href":"/cabbage3docs/docs/cabbage_widgets/button","docId":"cabbage_widgets/button","unlisted":false},{"type":"link","label":"ComboBox","href":"/cabbage3docs/docs/cabbage_widgets/combobox","docId":"cabbage_widgets/combobox","unlisted":false},{"type":"link","label":"GenTable","href":"/cabbage3docs/docs/cabbage_widgets/gentable","docId":"cabbage_widgets/gentable","unlisted":false},{"type":"link","label":"Groupbox","href":"/cabbage3docs/docs/cabbage_widgets/groupbox","docId":"cabbage_widgets/groupbox","unlisted":false},{"type":"link","label":"Image","href":"/cabbage3docs/docs/cabbage_widgets/image","docId":"cabbage_widgets/image","unlisted":false},{"type":"link","label":"Keyboard","href":"/cabbage3docs/docs/cabbage_widgets/keyboard","docId":"cabbage_widgets/keyboard","unlisted":false},{"type":"link","label":"Label","href":"/cabbage3docs/docs/cabbage_widgets/label","docId":"cabbage_widgets/label","unlisted":false},{"type":"link","label":"OptionButton","href":"/cabbage3docs/docs/cabbage_widgets/optionButton","docId":"cabbage_widgets/optionButton","unlisted":false},{"type":"link","label":"HorizontalSlider","href":"/cabbage3docs/docs/cabbage_widgets/horizontalSlider","docId":"cabbage_widgets/horizontalSlider","unlisted":false},{"type":"link","label":"VerticalSlider","href":"/cabbage3docs/docs/cabbage_widgets/verticalSlider","docId":"cabbage_widgets/verticalSlider","unlisted":false},{"type":"link","label":"RotarySlider","href":"/cabbage3docs/docs/cabbage_widgets/rotarySlider","docId":"cabbage_widgets/rotarySlider","unlisted":false},{"type":"link","label":"XY Pad","href":"/cabbage3docs/docs/cabbage_widgets/xyPad","docId":"cabbage_widgets/xyPad","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"link","label":"Custom User Interfaces","href":"/cabbage3docs/docs/customUIs","docId":"customUIs","unlisted":false},{"type":"category","label":"Opcodes","items":[{"type":"link","label":"Introduction","href":"/cabbage3docs/docs/cabbage_opcodes/intro","docId":"cabbage_opcodes/intro","unlisted":false},{"type":"link","label":"cabbageCreate","href":"/cabbage3docs/docs/cabbage_opcodes/cabbageCreate","docId":"cabbage_opcodes/cabbageCreate","unlisted":false},{"type":"link","label":"cabbageCreateFileName","href":"/cabbage3docs/docs/cabbage_opcodes/cabbageCreateFileName","docId":"cabbage_opcodes/cabbageCreateFileName","unlisted":false},{"type":"link","label":"cabbageDump","href":"/cabbage3docs/docs/cabbage_opcodes/cabbageDump","docId":"cabbage_opcodes/cabbageDump","unlisted":false},{"type":"link","label":"cabbageGet","href":"/cabbage3docs/docs/cabbage_opcodes/cabbageGet","docId":"cabbage_opcodes/cabbageGet","unlisted":false},{"type":"link","label":"cabbageGetFiles","href":"/cabbage3docs/docs/cabbage_opcodes/cabbageGetFiles","docId":"cabbage_opcodes/cabbageGetFiles","unlisted":false},{"type":"link","label":"cabbageGetValue","href":"/cabbage3docs/docs/cabbage_opcodes/cabbageGetValue","docId":"cabbage_opcodes/cabbageGetValue","unlisted":false},{"type":"link","label":"cabbageLoadState","href":"/cabbage3docs/docs/cabbage_opcodes/cabbageLoadState","docId":"cabbage_opcodes/cabbageLoadState","unlisted":false},{"type":"link","label":"cabbageSaveState","href":"/cabbage3docs/docs/cabbage_opcodes/cabbageSaveState","docId":"cabbage_opcodes/cabbageSaveState","unlisted":false},{"type":"link","label":"cabbageSendMessage","href":"/cabbage3docs/docs/cabbage_opcodes/cabbageSendMessage","docId":"cabbage_opcodes/cabbageSendMessage","unlisted":false},{"type":"link","label":"cabbageSet","href":"/cabbage3docs/docs/cabbage_opcodes/cabbageSet","docId":"cabbage_opcodes/cabbageSet","unlisted":false},{"type":"link","label":"cabbageSetValue","href":"/cabbage3docs/docs/cabbage_opcodes/cabbageSetValue","docId":"cabbage_opcodes/cabbageSetValue","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"html","value":"<hr style=\\"border: 0; border-top: 1px solid #CCC; margin: 0.5rem 0;\\" />","defaultStyle":true},{"type":"link","label":"JavaScript API","href":"/cabbage3docs/docs/CabbageJS_API","docId":"CabbageJS_API","unlisted":false}]},"docs":{"cabbage_opcodes/cabbageCreate":{"id":"cabbage_opcodes/cabbageCreate","title":"cabbageCreate","description":"This opcode will create a new widget based on a JSON object. Note that widgets generated in this way cannot be registered as parameters. This means they cannot be automated by the host. Note this opcode only runs at i-time.","sidebar":"docSidebar"},"cabbage_opcodes/cabbageCreateFileName":{"id":"cabbage_opcodes/cabbageCreateFileName","title":"cabbageCreateFileName","description":"Generates a likely-unique filename in a target directory using the requested extension. This opcode is useful when you need a valid and unique filename for testing. The opcode returns a full path (forward slashes) but does not create the file on disk. This opcode is i-time only, so it\'s best used in a dedicated instrument that will generate the filename when needed.","sidebar":"docSidebar"},"cabbage_opcodes/cabbageDump":{"id":"cabbage_opcodes/cabbageDump","title":"cabbageDump","description":"This opcode will print the JSON representation of a widget to the Csound console. This can be useful for debugging widgets.","sidebar":"docSidebar"},"cabbage_opcodes/cabbageGet":{"id":"cabbage_opcodes/cabbageGet","title":"cabbageGet","description":"This opcode queries the current value of a widget property, i.e,colour, bounds.top, height, etc. It can run at init-time or k-time and includes an optional output argument that triggers a value of 1 for one k-cycle whenever the widget value is updated or changed.","sidebar":"docSidebar"},"cabbage_opcodes/cabbageGetFiles":{"id":"cabbage_opcodes/cabbageGetFiles","title":"cabbageGetFiles","description":"Returns a list of files in a directory optionally filtered by extension. Useful for populating file lists for widgets (e.g., comboboxes) or for scanning resource folders. This opcode works at i-time only. Therefore it is best used in an instrument that is called when the files are needed.","sidebar":"docSidebar"},"cabbage_opcodes/cabbageGetValue":{"id":"cabbage_opcodes/cabbageGetValue","title":"cabbageGetValue","description":"This opcode queries the current value of a widget. It can run at init-time or k-time and includes an optional output argument that triggers a value of 1 for one k-cycle whenever the widget value is updated or changed.","sidebar":"docSidebar"},"cabbage_opcodes/cabbageLoadState":{"id":"cabbage_opcodes/cabbageLoadState","title":"cabbageLoadState","description":"This opcode loads widget values from a previously saved JSON state file created by the cabbageSaveState opcode. All matching widget channels will be updated with their saved values. Unlike Cabbage 2, all widget state is saved and restored, not only the current parameter values. This opcode works at i-time only. Therefore it\'s best used in an instrument that is called when you want to load state.","sidebar":"docSidebar"},"cabbage_opcodes/cabbageSaveState":{"id":"cabbage_opcodes/cabbageSaveState","title":"cabbageSaveState","description":"This opcode saves the current state of all widget values to a JSON file. The saved state can later be restored using the cabbageLoadState opcode. This is useful for creating preset systems or saving/loading configurations. This opcode works at i-time only. Therefore it is best used in an instrument that is called when you want to save state.","sidebar":"docSidebar"},"cabbage_opcodes/cabbageSendMessage":{"id":"cabbage_opcodes/cabbageSendMessage","title":"cabbageSendMessage","description":"This opcode sends arbitrary JSON data directly to the frontend, allowing you to create custom messages for specialized UI interactions or data visualization. Unlike other Cabbage opcodes that update specific widget properties, cabbageSendMessage gives you complete control over the message format and content.","sidebar":"docSidebar"},"cabbage_opcodes/cabbageSet":{"id":"cabbage_opcodes/cabbageSet","title":"cabbageSet","description":"This opcode sets a widget property, i.e,colour.fill, visible, opacity, etc. It can run at init-time or k-time. The k-time variant must use a trigger to initiate the update (see below).","sidebar":"docSidebar"},"cabbage_opcodes/cabbageSetValue":{"id":"cabbage_opcodes/cabbageSetValue","title":"cabbageSetValue","description":"This opcode sets the current value of a widget and can be executed at either init-time or k-time. When called, it immediately writes the incoming value to a Csound channel and also places the value into a FIFO queue, which the frontend reads to update the UI.","sidebar":"docSidebar"},"cabbage_opcodes/intro":{"id":"cabbage_opcodes/intro","title":"Introduction","description":"Cabbage ships with a number of opcodes that provide an interface the each widget\'s underlying JSON object. Any property of of a widget\'s props member can be accessed or modified using the cabbageGet/cabbageSet opcodes. A widget\'s value can be queried and set using the cabbageGetValue and cabbageSetValue opcodes. A number of utility opcodes are also included. Details of all opcodes can be found in the following pages.","sidebar":"docSidebar"},"cabbage_widgets/button":{"id":"cabbage_widgets/button","title":"Button","description":"button creates a button on screen that can be used for a whole range of tasks. The \\"channel\\" string identifies the channel on which Cabbage will communicate with Csound.","sidebar":"docSidebar"},"cabbage_widgets/button.style":{"id":"cabbage_widgets/button.style","title":"button.style","description":"- Colors accept named CSS colors, hex (e.g., #RRGGBB or #RRGGBBAA), or rgb/rgba strings."},"cabbage_widgets/checkbox":{"id":"cabbage_widgets/checkbox","title":"CheckBox","description":"checkBox creates a checkBox on screen that can be used for a whole range of tasks. The \\"channel\\" string identifies the channel on which Cabbage will communicate with Csound."},"cabbage_widgets/checkbox.style":{"id":"cabbage_widgets/checkbox.style","title":"checkbox.style","description":"Notes"},"cabbage_widgets/combobox":{"id":"cabbage_widgets/combobox","title":"ComboBox","description":"combobox creates a drop-down list of items which users can choose from. Once the user selects an item, the index of their selection will be sent to Csound on a channel named by the channel string.","sidebar":"docSidebar"},"cabbage_widgets/combobox.style":{"id":"cabbage_widgets/combobox.style","title":"combobox.style","description":"- Colors accept named CSS colors, hex (e.g., #RRGGBB or #RRGGBBAA), or rgb/rgba strings."},"cabbage_widgets/form":{"id":"cabbage_widgets/form","title":"Form","description":"form creates the main application window. pluginId is the only required property, while caption will only be used when running an instrument as a standalone. The default values for size are 600x300. There can only be one form per instrument, and it has the unique channel name of MainForm.","sidebar":"docSidebar"},"cabbage_widgets/form.style":{"id":"cabbage_widgets/form.style","title":"form.style","description":"- The form background color is controlled by style.fill."},"cabbage_widgets/gentable":{"id":"cabbage_widgets/gentable","title":"GenTable","description":"genTable creates a table widget on screen that can be used to display the contents of a function table or the contents of a file. When selectableRegions is enabled, users can interactively select portions of the waveform by clicking and dragging.","sidebar":"docSidebar"},"cabbage_widgets/gentable.style":{"id":"cabbage_widgets/gentable.style","title":"gentable.style","description":"- Background controls the area behind the waveform."},"cabbage_widgets/groupbox":{"id":"cabbage_widgets/groupbox","title":"Groupbox","description":"groupBox creates a simple on-screen grouping box, i.e., a bounding rectangle that you can use to organise widgets within.","sidebar":"docSidebar"},"cabbage_widgets/groupbox.style":{"id":"cabbage_widgets/groupbox.style","title":"groupbox.style","description":"- Colors accept named CSS colors, hex (e.g., #RRGGBB or #RRGGBBAA), or rgb/rgba strings."},"cabbage_widgets/horizontalSlider":{"id":"cabbage_widgets/horizontalSlider","title":"HorizontalSlider","description":"horizontalSlider allows users to adjust a value by sliding a handle horizontally. This component is useful for controlling parameters in real-time.","sidebar":"docSidebar"},"cabbage_widgets/horizontalSlider.style":{"id":"cabbage_widgets/horizontalSlider.style","title":"horizontalSlider.style","description":"- \\"auto\\" values are calculated from the widget bounds to scale appropriately."},"cabbage_widgets/image":{"id":"cabbage_widgets/image","title":"Image","description":"image creates an on-screen image. Users can interact with an image via mouse events if a channel name has been assigned.","sidebar":"docSidebar"},"cabbage_widgets/image.style":{"id":"cabbage_widgets/image.style","title":"image.style","description":"- When file is set, the image is rendered and backgroundColor is not visible; otherwise a filled rectangle is shown."},"cabbage_widgets/index":{"id":"cabbage_widgets/index","title":"List of Cabbage widgets","description":""},"cabbage_widgets/intro":{"id":"cabbage_widgets/intro","title":"Cabbage Widgets","description":"While it is possible to create custom widgets using any JavaScript/HTML framework, Cabbage also provides a set of built-in widgets.","sidebar":"docSidebar"},"cabbage_widgets/keyboard":{"id":"cabbage_widgets/keyboard","title":"Keyboard","description":"keyboard will display a keyboard that will send MIDI information to your Csound instrument. This component can be used together with a hardware controller.","sidebar":"docSidebar"},"cabbage_widgets/keyboard.style":{"id":"cabbage_widgets/keyboard.style","title":"keyboard.style","description":"- Keyboard visuals (white/black keys, keydown color) are configured under the color.* object on the widget, not in style."},"cabbage_widgets/label":{"id":"cabbage_widgets/label","title":"Label","description":"label creates a simple on-screen text label. Users can interact with the label via mouse down events if a channel name has been assigned.","sidebar":"docSidebar"},"cabbage_widgets/label.style":{"id":"cabbage_widgets/label.style","title":"label.style","description":"- For labels, typography is part of the style object."},"cabbage_widgets/numberSlider":{"id":"cabbage_widgets/numberSlider","title":"NumberSlider","description":"numberSlider allows users to adjust a numeric value by clicking and dragging up or down. This component displays the current value as text and is useful for precise parameter control in a compact space."},"cabbage_widgets/numberSlider.style":{"id":"cabbage_widgets/numberSlider.style","title":"numberSlider.style","description":"Notes"},"cabbage_widgets/optionButton":{"id":"cabbage_widgets/optionButton","title":"OptionButton","description":"optionButton creates a button which users can use to toggle through a range of items. Pressing the button will send the index of that item to Csound on a channel named by the channel string.","sidebar":"docSidebar"},"cabbage_widgets/optionButton.style":{"id":"cabbage_widgets/optionButton.style","title":"optionButton.style","description":"- Colors accept named CSS colors, hex (e.g., #RRGGBB or #RRGGBBAA), or rgb/rgba strings."},"cabbage_widgets/properties/active":{"id":"cabbage_widgets/properties/active","title":"active","description":"Will deactivate a control if false is set (use 0/1 to update from Csound). Controls which are deactivated can still be updated from Csound."},"cabbage_widgets/properties/alpha":{"id":"cabbage_widgets/properties/alpha","title":"alpha","description":"A value between 0 and 1 will set the alpha blend value for the entire component. Can be useful if you need to fade widgets in and out."},"cabbage_widgets/properties/automatable":{"id":"cabbage_widgets/properties/automatable","title":"automatable","description":"Defaults to true. Determines if a widget is automatable by a host DAW. Automatable widgets show up as plug-in parameters in the host. Non-automatable widgets can still communicate with Csound but are not accessible by the host. Note that hosts don\'t allow this parameter to change dynamically. If you change this setting, the plugin will need to be reloaded."},"cabbage_widgets/properties/ballSize":{"id":"cabbage_widgets/properties/ballSize","title":"ballSize","description":"Sets the size (diameter) of the ball in the xyPad widget. The ball represents the current position on the X-Y plane. The default value is 20 pixels. The ball size affects the usable range of the pad - the ball\'s center is constrained to stay within the pad boundaries, which means larger balls will have their movement range reduced by half the ball size on each edge."},"cabbage_widgets/properties/bounds":{"id":"cabbage_widgets/properties/bounds","title":"bounds","description":"Integer values that set position and size on screen(in pixels)."},"cabbage_widgets/properties/caption":{"id":"cabbage_widgets/properties/caption","title":"caption","description":"The string passed to caption will be the string that appears on the main application window."},"cabbage_widgets/properties/channel":{"id":"cabbage_widgets/properties/channel","title":"channel","description":"In Cabbage 3, widgets use an array of channels instead of a single channel string. Each channel object includes an id that defines a unique channel name. Widgets in Cabbage 3 can have any number of channels attached to them. An optional eventType property specifies the type of interaction. A range object can define the minimum, maximum, and default values, along with the skew and step increment. The type is set to number by default. Each widget sets its own type, number or string."},"cabbage_widgets/properties/channelConfig":{"id":"cabbage_widgets/properties/channelConfig","title":"channelConfig","description":"Sets the preferred channel and bus layout. You can add as many configuration strings as you like, e.g., \\"2-2 2-1 1-1\\". You can also organise channels into buses by using a \'.\' operator. For example, the following string will group the inputs into a stereo bus, alongside a mono sidechain input: \\"2.1-2\\"."},"cabbage_widgets/properties/channelGentable":{"id":"cabbage_widgets/properties/channelGentable","title":"channelGentable","description":"The genTable has two default channels that provide information about the current selection. Note these channels are only valid if selectableRegions is set to true."},"cabbage_widgets/properties/channelXY":{"id":"cabbage_widgets/properties/channelXY","title":"channelXY","description":"Defines the channel configuration for the xyPad widget:"},"cabbage_widgets/properties/colour":{"id":"cabbage_widgets/properties/colour","title":"colour","description":"Colour properties can be any valid CSS or HTML color string. They can also accepts be hexadecimal strings, or RGB / RGBA array, where each channel ranges from 0 to 255. For example, \\"colour\\"[0, 0, 255]} or \\"colour\\"\\"\\"#ff0000\\"}."},"cabbage_widgets/properties/colourButton":{"id":"cabbage_widgets/properties/colourButton","title":"colourButton","description":"This property sets the main colour of a button. Colour properties themselves can be any valid CSS or HTML color string. They can also accepts be hexadecimal strings, or RGB / RGBA array, where each channel ranges from 0 to 255. For example, \\"colour\\"{\\"fill\\"{\\"on\\"\\"\\"#ff0000\\"}}."},"cabbage_widgets/properties/colourGentable":{"id":"cabbage_widgets/properties/colourGentable","title":"colourGentable","description":"Colour properties can be any valid CSS or HTML color string. They can also accepts be hexadecimal strings, or RGB / RGBA array, where each channel ranges from 0 to 255. For example, \\"colour\\"[0, 0, 255]} or \\"colour\\"\\"\\"#ff0000\\"}. fill sets the table colour, while stroke.outline sets the outline colour."},"cabbage_widgets/properties/colourKeyboard":{"id":"cabbage_widgets/properties/colourKeyboard","title":"colourKeyboard","description":"Colour properties can be any valid CSS or HTML color string. They can also accepts be hexadecimal strings, or RGB / RGBA array, where each channel ranges from 0 to 255. For example, \\"colour\\"[0, 0, 255]} or \\"colour\\"\\"\\"#ff0000\\"}. fill sets the table colour, while stroke.outline sets the outline colour."},"cabbage_widgets/properties/colourSliders":{"id":"cabbage_widgets/properties/colourSliders","title":"colourSliders","description":"Colour properties can be any valid CSS or HTML color string. They can also accepts be hexadecimal strings, or RGB / RGBA array, where each channel ranges from 0 to 255. For example, \\"colour\\"[0, 0, 255]} or \\"colour\\"\\"\\"#ff0000\\"}. fill sets the table colour, while stroke.outline sets the outline colour, while tracker.width will set teh width of the tracker in pixels."},"cabbage_widgets/properties/corners":{"id":"cabbage_widgets/properties/corners","title":"corners","description":"Sets the radius size of the widget\'s corners."},"cabbage_widgets/properties/debounceInterval":{"id":"cabbage_widgets/properties/debounceInterval","title":"debounceInterval","description":"Sets the minimum time (in milliseconds) to wait before sending a new parameter update. This helps prevent redundant updates by ensuring only one update is sent per interval, even if multiple changes occur quickly. The default is 50ms."},"cabbage_widgets/properties/enableDevTools":{"id":"cabbage_widgets/properties/enableDevTools","title":"enableDevTools","description":"Enables right-clicking of webview context menu. When this is disabled users will not be able to inspect your UI or access any of the browser dev tools. This is enabled by default."},"cabbage_widgets/properties/file":{"id":"cabbage_widgets/properties/file","title":"file","description":"This identifier is used across a number of widgets."},"cabbage_widgets/properties/fileImage":{"id":"cabbage_widgets/properties/fileImage","title":"fileImage","description":"This property sets the file to be displayed by the image widget. The following image formats should be supported on most systems: PNG, JPEG, GIF"},"cabbage_widgets/properties/filmStrip":{"id":"cabbage_widgets/properties/filmStrip","title":"filmStrip","description":"This property sets a film strip file to be displayed by the rotary slider widget. Fow now, only PNG images are supported. Images can be horizontal or vertical."},"cabbage_widgets/properties/font":{"id":"cabbage_widgets/properties/font","title":"font","description":"Sets the font size, family, colour and font alignment of the widget, where applicable."},"cabbage_widgets/properties/id":{"id":"cabbage_widgets/properties/id","title":"id","description":"This optional channel can be used to define a top-level line of communication between the instrument\u2019s UI and Csound. It is reserved for UI updates only. If omitted, it defaults to the first id from the channels array. Its primary purpose is to help produce clearer code, especially for widgets with multiple channels."},"cabbage_widgets/properties/items":{"id":"cabbage_widgets/properties/items","title":"items","description":"A list of items that will populate the comboBox/listBox or optionButton. Each item has a corresponding index value. The first item when selected will send a 0, the second item a 1, the third a 2 etc. If this identifier is left out default values of \\"Item 1\\", \\"Item  2\\", \\"Item  3\\", \\"Item 4\\" and \\"Item 5\\" will be used."},"cabbage_widgets/properties/keyWidth":{"id":"cabbage_widgets/properties/keyWidth","title":"keyWidth","description":"Set the width of the keys in pixels."},"cabbage_widgets/properties/label":{"id":"cabbage_widgets/properties/label","title":"label","description":"Configures the widget label displayed near the control."},"cabbage_widgets/properties/logger":{"id":"cabbage_widgets/properties/logger","title":"logger","description":"Enables file logging when in plugin mode. If \\"file\\" is left empty, the log file will default to the same name as the .csd file, but with a .log extension. The \\"replace\\" parameter determines whether Cabbage overwrites the log file on successive runs. File logging, along with this log overwriting, is enabled by default. Note the logger is only available in plugin builds."},"cabbage_widgets/properties/opacity":{"id":"cabbage_widgets/properties/opacity","title":"opacity","description":"The opacity attribute property can vary between 0 (fully transparent) and 1 (fully opaque)."},"cabbage_widgets/properties/package":{"id":"cabbage_widgets/properties/package","title":"package","description":"The package object is used to manage the resources required by your instruments when they are exported. The package accepts an array of src/dest pairs. The src value may be a relative or absolute path and can include file wildcards (for example,  or *)."},"cabbage_widgets/properties/pluginId":{"id":"cabbage_widgets/properties/pluginId","title":"pluginId","description":"this unique string must be four characters long. It is the ID given to your plugin when loaded by plugin hosts."},"cabbage_widgets/properties/populate":{"id":"cabbage_widgets/properties/populate","title":"populate","description":"Sets the type of file to search for, and the directories to look in. You can specify multiple file types by using the following pattern .txt;.csv;*.dat. If no directories are specified, the file browser will try to access the current working directory. Note it\'s always best to set the directories property from your Csound code in order to make sure the path is correct. For example, the following code will set the directory to that of the current .csd file."},"cabbage_widgets/properties/popup":{"id":"cabbage_widgets/properties/popup","title":"popup","description":"This property will set whether a slider\'s popup value box will appear. It\'s enabled by default."},"cabbage_widgets/properties/range":{"id":"cabbage_widgets/properties/range","title":"range","description":"the first 2 parameters are required. The rest are optional. The first two parameters let you set the minimum value and the maximum value. The next parameter determines the initial value of the slider. The 4th parameter allows you to adjust the skew factor. Tweaking the skew factor will cause the slider to output values in a non linear fashion. A skew of 0.5 will cause the slider to output values in an exponential fashion. A skew of 1 is the default value, which causes the slider to behave is a typical linear form. The final parameter sets the incremental step size for the slider."},"cabbage_widgets/properties/rangeGenTable":{"id":"cabbage_widgets/properties/rangeGenTable","title":"rangeGenTable","description":"Defines the range for the Y axis in a genTable widget."},"cabbage_widgets/properties/rangeXY":{"id":"cabbage_widgets/properties/rangeXY","title":"rangeXY","description":"Defines the ranges for both X and Y axes of the xyPad. Each axis has its own independent range with the following parameters:"},"cabbage_widgets/properties/sample":{"id":"cabbage_widgets/properties/sample","title":"sample","description":"This property sets the visible range of samples in a gentable. You can adjust these properties in real-time to enable zooming."},"cabbage_widgets/properties/sampleRange":{"id":"cabbage_widgets/properties/sampleRange","title":"sampleRange","description":"This property sets the visible range of samples in a gentable. You can adjust these properties in real-time to enable zooming."},"cabbage_widgets/properties/selectableRegions":{"id":"cabbage_widgets/properties/selectableRegions","title":"selectableRegions","description":""},"cabbage_widgets/properties/size":{"id":"cabbage_widgets/properties/size","title":"size","description":"integer values denoted the width and height of the form."},"cabbage_widgets/properties/tableNumber":{"id":"cabbage_widgets/properties/tableNumber","title":"tableNumber","description":"Sets the function table to be displayed by the genTable widget."},"cabbage_widgets/properties/text":{"id":"cabbage_widgets/properties/text","title":"text","description":"\\"string\\" will be the text that appears on the widget."},"cabbage_widgets/properties/textOnOff":{"id":"cabbage_widgets/properties/textOnOff","title":"textOnOff","description":"One string for on and one for off."},"cabbage_widgets/properties/textXY":{"id":"cabbage_widgets/properties/textXY","title":"textXY","description":"Sets the text labels for the X and Y axes of the xyPad. Each axis can have its own label displayed in the value boxes at the bottom of the widget. If the labels contain separators like /, |, or \\\\, they will be automatically split during conversion from Cabbage2 format."},"cabbage_widgets/properties/trackerWidth":{"id":"cabbage_widgets/properties/trackerWidth","title":"trackerWidth","description":"This property sets the width of a rotary slider\'s tracker."},"cabbage_widgets/properties/trackThickness":{"id":"cabbage_widgets/properties/trackThickness","title":"trackThickness","description":"Sets the thickness of the rotary slider track. Use a number (pixels) or \\"auto\\" to have it scale with the widget size."},"cabbage_widgets/properties/trackWidth":{"id":"cabbage_widgets/properties/trackWidth","title":"trackWidth","description":"Sets the width of the horizontal/vertical slider track. Use a number (pixels) or \\"auto\\" to have it scale with the widget size."},"cabbage_widgets/properties/value":{"id":"cabbage_widgets/properties/value","title":"value","description":"Value sets the initial state of the widget. In the case of a keyboard widget, it sets the leftmost key to appear."},"cabbage_widgets/properties/valueText":{"id":"cabbage_widgets/properties/valueText","title":"valueText","description":"Controls the numeric value display area inside the slider."},"cabbage_widgets/properties/valueTextBox":{"id":"cabbage_widgets/properties/valueTextBox","title":"valueTextBox","description":"When set to 1 this property will cause a text box to appear with the slider\'s values. The value box can be double-clicked to allow entering of precision values. Hitting enter will send the value to the widget."},"cabbage_widgets/properties/velocity":{"id":"cabbage_widgets/properties/velocity","title":"velocity","description":"Sets the sensitivity of the slider to mouse movement. The value passed should be in the range of 1 upwards. Note that velocity mode will only work if the range of the slider is greater than the distance in pixels between min and max."},"cabbage_widgets/properties/visible":{"id":"cabbage_widgets/properties/visible","title":"visible","description":"A value of true will cause the widget to become invisible (use 0/1 to update from Csound). Widgets have their visibility set to true by default."},"cabbage_widgets/properties/zindex":{"id":"cabbage_widgets/properties/zindex","title":"zindex","description":"Sets the stacking order of the widget. Widgets with higher zIndex values will appear in front of widgets with lower values. Default is 1."},"cabbage_widgets/rotarySlider":{"id":"cabbage_widgets/rotarySlider","title":"RotarySlider","description":"rotarySlider allows users to adjust a value by sliding a handle vertically. This component is useful for controlling parameters in real-time.","sidebar":"docSidebar"},"cabbage_widgets/rotarySlider.style":{"id":"cabbage_widgets/rotarySlider.style","title":"rotarySlider.style","description":"- \\"auto\\" values are calculated from the widget bounds to scale appropriately."},"cabbage_widgets/verticalSlider":{"id":"cabbage_widgets/verticalSlider","title":"VerticalSlider","description":"verticalSlider allows users to adjust a value by sliding a handle vertically. This component is useful for controlling parameters in real-time.","sidebar":"docSidebar"},"cabbage_widgets/verticalSlider.style":{"id":"cabbage_widgets/verticalSlider.style","title":"verticalSlider.style","description":"- \\"auto\\" values are calculated from the widget bounds to scale appropriately."},"cabbage_widgets/xyPad":{"id":"cabbage_widgets/xyPad","title":"XY Pad","description":"xyPad creates a 2-dimensional X-Y pad controller that allows simultaneous control of two parameters. A draggable ball represents the current position, with independent channels for X and Y axes.","sidebar":"docSidebar"},"cabbage_widgets/xyPad.style":{"id":"cabbage_widgets/xyPad.style","title":"xyPad.style","description":"- Colors accept named CSS colors, hex (e.g., #RRGGBB or #RRGGBBAA), or rgb/rgba strings."},"cabbage3":{"id":"cabbage3","title":"What\'s new in v.3","description":"Cabbage 3","sidebar":"docSidebar"},"CabbageJS_API":{"id":"CabbageJS_API","title":"Cabbage JS API","description":"Cabbage API","sidebar":"docSidebar"},"customUIs":{"id":"customUIs","title":"Custom User Interfaces","description":"Custom UI\'s","sidebar":"docSidebar"},"download":{"id":"download","title":"Download and Install","description":"Download and Install","sidebar":"docSidebar"},"faqs":{"id":"faqs","title":"faqs","description":"I can\'t see any interface when I compile an instrument from my .csd file in VS Code?"},"intro":{"id":"intro","title":"Cabbage v3 Documentation","description":"Welcome to the Cabbage 3 documentation. Here you\u2019ll find everything you need to get started and make the most of the platform, including guides on downloading and installing the software, an overview of what\u2019s new, and instructions for using the VS Code extension for seamless integration. You\u2019ll also find detailed reference pages for all supported widgets and opcodes, a short guide to developing custom user interfaces, and an overview of the Cabbage 3 JavaScript API.","sidebar":"docSidebar"},"using_cabbage/commands":{"id":"using_cabbage/commands","title":"Commands & Settings","description":"Available commands and settings in Cabbage 3","sidebar":"docSidebar"},"using_cabbage/exporting_instruments":{"id":"using_cabbage/exporting_instruments","title":"Exporting Instruments","description":"How to export instruments","sidebar":"docSidebar"},"using_cabbage/media_files":{"id":"using_cabbage/media_files","title":"Working with Media Files","description":"Managing media files in Cabbage 3","sidebar":"docSidebar"},"using_cabbage/multichannel":{"id":"using_cabbage/multichannel","title":"Working with Multichannel Instruments","description":"Managing multichannel setups","sidebar":"docSidebar"},"using_cabbage/overview":{"id":"using_cabbage/overview","title":"Overview","description":"Overview","sidebar":"docSidebar"},"using_cabbage/reserved_channels":{"id":"using_cabbage/reserved_channels","title":"Reserved Channels","description":"Reserved channels in Cabbage 3 are special string or numeric channels automatically populated by the Cabbage engine. Some provide system-specific directory paths, while others convey environmental or system information, such as window dimensions or plugin status. These channels give you a cross-platform way to access common system directories or runtime data without hardcoding values. Cabbage 3 works with absolute file paths, so be sure to derive all file paths from known locations where appropriate.","sidebar":"docSidebar"},"using_cabbage/running_cabbage":{"id":"using_cabbage/running_cabbage","title":"Running Cabbage","description":"Running the Cabbage server","sidebar":"docSidebar"},"using_cabbage/working_with_instruments":{"id":"using_cabbage/working_with_instruments","title":"Working with Instruments","description":"How to work with Cabbage instruments","sidebar":"docSidebar"}}}}')}}]);